CREATE DATABASE posts;
\c posts
CREATE TABLE post (id INT, nombre_de_usuario VARCHAR(100), fecha_de_creacion DATE, contenido VARCHAR(200), descripcion VARCHAR(200), PRIMARY KEY (id));
ALTER TABLE post ADD titulo VARCHAR(100);
UPDATE post SET titulo='Galletas de chocolate' WHERE id=1;
UPDATE post SET titulo='Kuchen de manzana' WHERE id=2;
UPDATE post SET titulo='Carne mechada' WHERE id=3;
DELETE FROM post WHERE nombre_de_usuario='Carlos';
CREATE TABLE comentarios (id INT, fecha_y_hora_de_creacion TIMESTAMP, contenido VARCHAR(200), FOREIGN KEY (id) REFERENCES post (id));
